mapper.update:
update books
set name = #{name}, holding_type_id = #{holdingTypeId}, status_id = #{statusId},
    authors = #{authors}, topics = #{topics}, description = #{description},
    year_published = #{yearPublished}, field_id = #{fieldId}
where id = #{id};

mapper.select:
select b.id, b.owner_id, b.name, coalesce(b.authors, '') as authors, coalesce(b.topics, '') as topics, coalesce(b.description, '') as description, coalesce(cast(b.year_published as varchar), '') as year_published, bht.name as holding_type, bst.name as status, f.name as field, c.name as city, round(avg(coalesce(r.score, 0)), 2) as owner_avg_rating from books b left join book_holding_types bht on b.holding_type_id = bht.id left join book_status_types bst on b.status_id = bst.id left join fields f on b.field_id = f.id left join users u on b.owner_id = u.id left join cities c on u.city_id = c.id left join reviews r on r.reviewee_id = u.id
where
lower(b.name) LIKE '%' || lower(#{name}) || '%'
AND lower(authors) LIKE '%' || lower(#{author}) || '%'
AND lower(topics) LIKE '%' || lower(#{topic}) || '%'
AND year_published = #{yearPublished}
AND f.id = #{fieldId}
AND c.id = #{cityId}
group by b.id, b.owner_id, b.name, b.authors, b.topics, b.description, b.year_published, holding_type, status, field, city order by b.name asc, owner_avg_rating desc
